import React, { Component } from "react";
import "./Home.css";

import Header from "../../common/header/Header";
import BaseComponent from "./base-component/BaseComponent";
import Card from "./card/Card";
import Map from "./map/Map";
import Sign from "./sign/Sign";
import UserLists from "./user-lists/UserLists";
import Footer from "../../common/footer/Footer";

export default class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      signStatus: 0,
      count: 0,
      userList: [
        {
          id: null,
          firstName: null,
          lastName: null,
          active: null,
        },
      ],
    };
  }

  checkSign = () => {
    if (this.state.count !== 0) {
      console.log(this.state.signStatus);
      return <UserLists />;
    }
    return;
  };

  saveUser = () => {
    this.setState({signStatus:1});
  };

  render() {
    return (
      <div className="container-fluid">
        <div>
          <Header className="header-align container-fluid" />
        </div>

        <BaseComponent />
        <Card />
        <div className="text-center home-align">
          <Map />
        </div>
        <div className="row border home-align">
          <Sign saveUser={this.saveUser} />
        </div>
        <div className="row text-center">
          <div className="col-12">
            <button type="button" class="btn btn-primary">
              Show list users
            </button>
          </div>
        </div>
        <div className="row">{this.checkSign()}</div>

        <Footer />
      </div>
    );
  }
}
