import React, { Component } from "react";
import "./Home.css";

import Header from "../../common/header/Header";
import BaseComponent from "./base-component/BaseComponent";
import Card from "./card/Card";
import Map from "./map/Map";
import Sign from "./sign/Sign";
import UserLists from "./user-lists/UserLists";
import Footer from "../../common/footer/Footer";

export default class Home extends Component {
  state = {
    signStatus: 1,
  };

  renderUserList = () => (
    <div class="table-responsive">
      <table class="table">
        <caption>List of users</caption>
        <thead>
          <tr>
            <th scope="col">#</th>
            <th scope="col">First</th>
            <th scope="col">Last</th>
            <th scope="col">Handle</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <th scope="row">1</th>
            <td>Mark</td>
            <td>Otto</td>
            <td>@mdo</td>
          </tr>
          <tr>
            <th scope="row">2</th>
            <td>Jacob</td>
            <td>Thornton</td>
            <td>@fat</td>
          </tr>
          <tr>
            <th scope="row">3</th>
            <td>Larry</td>
            <td>the Bird</td>
            <td>@twitter</td>
          </tr>
        </tbody>
      </table>
    </div>
  );

  checkSign = () => {
    if (this.state.signStatus === 0) {
      return this.renderUserList;
    }
  };

  render() {
    return (
      <div className="container-fluid">
        <div>
          <Header className="header-align container-fluid" />
        </div>

        <BaseComponent />
        <Card />
        <div className="text-center home-align">
          <Map />
        </div>
        <div className="row border home-align">
          <Sign />
        </div>
        <div className="row">{this.checkSign()}</div>
        <Footer />
      </div>
    );
  }
}
