import React, { Component } from 'react';

export default class ButtonChange extends Component {
    constructor() {
        super();

        this.state={
            string: 'Hello Richard'
        }
    }

    render() {
        return (
            <div>
                <p>{this.state.string}</p>
                <button onClick={()=>{this.setState.string="Welcome to React JS"}}>Change</button>
            </div>
        )
    }
}
